import React, { Component } from 'react'
import axios from 'axios'
import {Link} from 'react-router-dom'

export default class PokedexPage extends Component {
    
    state = {
        data : [],
        name : "",
        sprite_default : "",
        sprite_back : "",
        page : ""
    }
    
    async componentDidMount() {

        if (this.props.match.params.page) {
           const res = await axios.get('https://pokeapi.co/api/v2/pokemon/' + this.props.match.params.page);
            this.setState({
                data : res.data,
                name : res.data.name,
                sprite_default : res.data.sprites.front_default,
                page: this.props.match.params.page
            })
            console.log(this.state)
        }

    }

    render() {
        return (
            <div>
                <div class="card" >
                <div class="card-body">


                    <div className='col'>
                        <h4 className="d-flex justify-content-center" style={{fontFamily:"Ketchum"}}>{this.state.name[0].toUpperCase()}</h4>
                    </div>
                    <div className='col'></div>

                    
                    
                    <img src={this.state.sprite_default} alt="not available"/>
                    
                </div>
                </div>
            </div>
        )
    }
}
