import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import axios from 'axios'
import { withRouter } from "react-router"

class CreateTeam extends Component {

    state = {
        users: [],
        userSelected: '',
        name: '',
        team: [],
        editing: false,
        _id:'',
        teams: []
    }


    async componentDidMount() {
        const res = await axios.get('http://localhost:4000/api/users')
        this.setState({
            users: res.data,
            userSelected: res.data[0].username
        })

        const res2 = await axios.get('http://localhost:4000/api/teams')
        this.setState({
            teams: res
        })


        if (this.props.match.params.id) {
            const res = await axios.get('http://localhost:4000/api/teams/' + this.props.match.params.id);
            this.setState({
                name: res.data.name,
                team: res.data.team,
                userSelected: res.data.user,
                editing: true,
                _id: this.props.match.params.id
            })
        }

        console.log(this.state)

    }

    render() {
        return (
            <div >
                <div className='row'>

                    <div className='col-4'>
                        
                        <input id="name" className='form-control' type="text" placeholder="Team's name" /> <br />

                        <input id="pokemon1" className='form-control inputPokemon' type="text" readOnly="true" placeholder='Pokemon 1' /> <br />
                        <input id="pokemon2" className='form-control inputPokemon' type="text" readOnly="true" placeholder='Pokemon 2' /> <br />
                        <input id="pokemon3" className='form-control inputPokemon' type="text" readOnly="true" placeholder='Pokemon 3' /> <br />
                        <input id="pokemon4" className='form-control inputPokemon' type="text" readOnly="true" placeholder='Pokemon 4' /> <br />
                        <input id="pokemon5" className='form-control inputPokemon' type="text" readOnly="true" placeholder='Pokemon 5' /> <br />
                        <input id="pokemon6" className='form-control inputPokemon' type="text" readOnly="true" placeholder='Pokemon 6' /> <br />

                    </div>

                    <div className='col'></div>

                </div>
            </div>

        )
    }
}

export default withRouter(CreateTeam);
